mapper.select:
select count(*) from ( select t.billnumber, t.goodid, t.geelybillnumber, t.geelycount, t.batch, t.count, t.boxcount,t.startname, t.startnumber,t.endname, t.endnumber, t.routetype, t.carnumber, t.driver, t.phone, t.carriername, t.cartypename,t.highlength, t.highheight, t.lowlength, t.lowheight, t.carwidth, t.money, t.remarks, t.username, t.createtime from transportbillcache t,good g,supplier s where t.goodid=g.id and g.supplierid=s.id
and g.goodcode like '%${goodcode}%'
and g.goodname like '%${goodname}%'
and s.suppliercode like '%${suppliercode}%'
and s.suppliername like '%${suppliername}%'
and t.billnumber like '%${billnumber}%'
and t.geelybillnumber like '%${geelybillnumber}%'
and t.carnumber =#{carnumber}
and to_days(t.createtime) >= to_days(#{dateStart})
and to_days(t.createtime) <= to_days(#{dateEnd})
and t.cartypename =#{cartypename}
and t.carriername =#{carriername}
) as a

select t.billnumber,t.startname, t.startnumber,t.endname, t.endnumber, t.routetype, t.carnumber, t.driver, t.phone, t.carriername, t.cartypename,t.highlength, t.highheight, t.lowlength, t.lowheight, t.carwidth, t.money, t.remarks, t.username, t.createtime from transportbillcache t,good g,supplier s where t.goodid=g.id and g.supplierid=s.id
and t.billnumber like '%${billnumber}%'
and t.geelybillnumber like '%${geelybillnumber}%'
and t.carnumber =#{carnumber}
and to_days(t.createtime) >= to_days(#{dateStart})
and to_days(t.createtime) <= to_days(#{dateEnd})
and t.cartypename =#{cartypename}
and t.carriername =#{carriername}
group by t.billnumber order by t.createtime desc,t.billnumber limit #{index},30

select count(*) from ( select t.billnumber,t.startname, t.startnumber,t.endname, t.endnumber, t.routetype, t.carnumber, t.driver, t.phone, t.carriername, t.cartypename,t.highlength, t.highheight, t.lowlength, t.lowheight, t.carwidth, t.money, t.remarks, t.username, t.createtime from transportbillcache t,good g,supplier s where t.goodid=g.id and g.supplierid=s.id
and t.billnumber like '%${billnumber}%'
and t.geelybillnumber like '%${geelybillnumber}%'
and t.carnumber =#{carnumber}
and to_days(t.createtime) >= to_days(#{dateStart})
and to_days(t.createtime) <= to_days(#{dateEnd})
and t.cartypename =#{cartypename}
and t.carriername =#{carriername}
group by t.billnumber ) as a

